--insert into recensione (voto, fotopath, titolo, descr, data, id_utente, id_elemento) values (5,"456ytfdwe456.PNG","Molto bene, peccato per il blacklight bleeding","Schermo fantastico, lo utilizzo con il mio MBP da 15 e sono davvero soddisfatto. C'è tantissimo spazio per lavorare sullo schermo. Piccolo difetto sui neri che non sono profondissimi a causa di un leggero blacklight bleeding.",1495470162,1,4);
--insert into recensione (voto, fotopath, titolo, descr, data, id_utente, id_elemento) values (3,"q54364y5454tr.jpg","Fantastico ma prezzo esagerato","L'ho comprato per cominciare l'università ed avere un pc che mi accompagnasse per 5 anni senza fare una piega. Dopo quasi 4 anni non posso lamentarmi di nulla. Se dura ancora 3 anni, tutti i soldi pagati saranno stati spesi bene.",1495470162,2,5);
--insert into recensione (voto, fotopath, titolo, descr, data, id_utente, id_elemento) values (5,"3456ygf456ythg.jpg","Uno dei migliori TV 4K per rapporto qualità/prezzo","Ottimo, davvero consigliato. La serie 6000 offre un processore di immagini quad core che renderizza le immagini veramente bene. Anche video e foto in full hd sembrano acquisire una nuova definizione. Colori e bilanciamento dei bianchi molto buoni. Funzioni smart tv da migliorare.",1495470162,1,8);
